  pub fn invoke(&self) -> solana_program_error::ProgramResult {
    self.invoke_signed(&[])
  }
  #[allow(clippy::clone_on_copy)]
  #[allow(clippy::vec_init_then_push)]
  pub fn invoke_signed(&self, signers_seeds: &[&[&[u8]]]) -> solana_program_error::ProgramResult {
          let args = UnwrapInstructionArgs {
                                                              amount: self.instruction.amount.clone().expect("amount is not set"),
                                    };
        let instruction = UnwrapCpi {
        __program: self.instruction.__program,
                  
          signer: self.instruction.signer.expect("signer is not set"),
                  
          payer: self.instruction.payer.expect("payer is not set"),
                  
          sender_ore: self.instruction.sender_ore.expect("sender_ore is not set"),
                  
          sender_store: self.instruction.sender_store.expect("sender_store is not set"),
                  
          ore_mint: self.instruction.ore_mint.expect("ore_mint is not set"),
                  
          store_mint: self.instruction.store_mint.expect("store_mint is not set"),
                  
          stake: self.instruction.stake.expect("stake is not set"),
                  
          stake_tokens: self.instruction.stake_tokens.expect("stake_tokens is not set"),
                  
          treasury: self.instruction.treasury.expect("treasury is not set"),
                  
          treasury_tokens: self.instruction.treasury_tokens.expect("treasury_tokens is not set"),
                  
          vault: self.instruction.vault.expect("vault is not set"),
                  
          vault_tokens: self.instruction.vault_tokens.expect("vault_tokens is not set"),
                  
          system_program: self.instruction.system_program.expect("system_program is not set"),
                  
          token_program: self.instruction.token_program.expect("token_program is not set"),
                  
          associated_token_program: self.instruction.associated_token_program.expect("associated_token_program is not set"),
                  
          ore_program: self.instruction.ore_program.expect("ore_program is not set"),
                          __args: args,
            };
    instruction.invoke_signed_with_remaining_accounts(signers_seeds, &self.instruction.__remaining_accounts)
  }
